import express from "express";
import deleteOrders from "../controllers/orders/DeleteOrders.js";
import AllOrders from "../controllers/orders/GetOrders.js";
import GetOrdersInfo from "../controllers/orders/GetOrdersInfo.js";
import getRestroOrders from "../controllers/orders/GetRestroOrders.js";
import getSpecOrders from "../controllers/orders/GetSpeOrders.js";
import createOrderFromCart from "../controllers/orders/PostOrders.js";
import { updateOrders, updateOrdersToDelivered, } from "../controllers/orders/updateOrders.js";
import authorize from "../middleware/authorize.js";
const router = express.Router({ mergeParams: true });
router.post("/", authorize, createOrderFromCart);
router.get("/", authorize, AllOrders);
router.get("/:orderId", authorize, getSpecOrders);
router.delete("/:orderId", authorize, deleteOrders);
router.get("/restaurant/:restaurantId", authorize, getRestroOrders);
router.get("/info/:orderId", authorize, GetOrdersInfo);
router.patch("/:orderId", authorize, updateOrders);
router.patch("/delivered/:orderId", authorize, updateOrdersToDelivered);
export default router;
